import React from 'react';
import { View, Text, TouchableOpacity, ScrollView, StyleSheet, Image } from 'react-native'
import { Dimensions } from 'react-native';
import SideSwipe from 'react-native-sideswipe';
import { AntDesign } from '@expo/vector-icons';
import Screen from '../assets/Screen.png'
import { getAuth, onAuthStateChanged } from 'firebase/auth';
import AnimatedLoader from "react-native-animated-loader";
import {LogBox} from 'react-native';
LogBox.ignoreAllLogs()

const window = Dimensions.get('window')
const Onboarding = (props) => {
    const [index, setIndex] = React.useState(1)
    const [user, setUser] = React.useState(null)
    const navigation = props.navigation
    const [data, setData] = React.useState([
        0, 1, 2, 3
    ])
    React.useEffect(() => {
        const auth = getAuth();
        onAuthStateChanged(auth, user => {
            if (user) {
                setUser(user);
                navigation.navigate('Dashboard')
            } else {
                setUser('ok')
            }
        })
    }, [])

    return (
        <ScrollView>
            <View style={{
                backgroundColor: 'black'
            }}>
                <SideSwipe
                    index={index}
                    itemWidth={window.width}
                    style={{}}
                    data={data}
                    contentOffset={window.width}
                    onIndexChange={index => {
                        setIndex(index);
                    }}
                    renderItem={({ itemIndex, currentIndex, item, animatedValue }) => (
                        <Slider
                            {...item}
                            index={itemIndex}
                            currentIndex={currentIndex}
                            animatedValue={animatedValue}
                        />
                    )} />
            </View>
            <View style={{
                height: 300,
                padding: 30,
                marginBottom: 30
            }}>
                <Text style={{
                    fontSize: 26
                }}>Track your mood and {'\n'} reflect on your day</Text>
                <Text style={{
                    fontSize: 17,
                    marginTop: 5,
                    color: '#0000008e'
                }}>Get an overview of how you are performing
                    and motivate yourself to achieve even moment</Text>
                <View style={{
                    height: 100,
                    width: '100%',
                    marginTop: 50,
                    flexDirection: 'row',
                    justifyContent: 'center', alignItems: 'center'
                }}>
                    <TouchableOpacity onPress={() => navigation.navigate('SignUp')} style={{
                        flex: 1,
                    }}>
                        <Text style={{ fontSize: 18 }}>Skip</Text>
                    </TouchableOpacity>
                    <View style={{
                        flex: 1,
                        flexDirection: 'row',
                    }}>

                        <View style={[styles.view, { backgroundColor: index == 0 ? 'black' : '#0000008e' }]}></View>
                        <View style={[styles.view, { backgroundColor: index == 1 ? 'black' : '#0000008e' }]}></View>
                        <View style={[styles.view, { backgroundColor: index == 2 ? 'black' : '#0000008e' }]}></View>
                        <View style={[styles.view, { backgroundColor: index == 3 ? 'black' : '#0000008e' }]}></View>
                    </View>
                    <TouchableOpacity onPress={() => {
                        if (index < 3) {
                            setIndex(index + 1)
                        } else {
                            navigation.navigate('SignUp')
                        }
                    }} style={{
                        height: 60,
                        width: 60,
                        backgroundColor: '#FC444B',
                        borderRadius: 20,
                        justifyContent: 'center',
                        alignItems: 'center',
                    }}>
                        <AntDesign name="arrowright" size={24} color="#ffff" />
                    </TouchableOpacity>
                </View>
            </View>
            {
                user == 'ok' ? (
                    <View></View>
                ) :
                    user && user.uid ? (
                        <View></View>
                    ) : (
                        <AnimatedLoader
                            visible={true}
                            overlayColor="rgba(255,255,255,0.75)"
                            source={require("../assets/9997-infinity-loader.json")}
                            animationStyle={{
                                height: 100, width: 100,
                            }}
                            speed={1}
                        >
                            <Text>Loading...</Text>
                        </AnimatedLoader>
                    )
            }
        </ScrollView>
    );
};

export default Onboarding;
const styles = StyleSheet.create({
    view: {
        width: 20,
        height: 4,
        margin: 2,
        backgroundColor: 'black',
        borderRadius: 1
    }
})
const Slider = ({ item }) => {
    return (
        <Image source={Screen} style={{
            width: window.width,
            height: window.height / 2 + 100,
        }} />
    )
}
