                                <Unorderedlist>
                                    <Text style={styles.nearbyTextDescr}>
                                        500m away from Sai Baba Mandir
                                    </Text>
                                </Unorderedlist>
                                <Unorderedlist>
                                    <Text style={styles.nearbyTextDescr}>
                                        200m away from Shirdi Bus Stop
                                    </Text>
                                </Unorderedlist>

                            </View>
                        </View>
                        <View style={styles.contentTop}>
                            <Text style={styles.hotelName}>Reviews </Text>
                            <View style={styles.contentTopLeftBox2}>
                                <TouchableOpacity>
                                    <AntDesign name="star" size={15} color="white" />
                                </TouchableOpacity>
                                <Text style={styles.contentTopLeftBoxText}>{Hotel ? Hotel[0].ratings : '0'}</Text>
                            </View>
                        </View>
                        {
                            Ratings ? (
                                Ratings.map((doc, i) => {
                                    if (i < limit) {
                                        return <HotelMember doc={doc} key={i}/>
                                    } else {
                                        return <View key={i}></View>
                                    }
                                })
                            ) : (
                                <ActivityIndicator size="large" color="#FA454B" />
                            )
                        }
                        <View style={{ alignItems: 'center', marginBottom: 30 }}>
                            {
                                Ratings && Ratings.length > limit ? (
                                    <TouchableOpacity onPress={() =>{
                                        setLimit(limit+3);
                                    }}>
                                        <View style={styles.showMoreButton}>
                                            <Text style={styles.showMoreButtonText}>
                                                Show More
                                            </Text>
                                        </View>
                                    </TouchableOpacity>
                                ):(
                                    <View>
                                    </View>
                                )
                            }
                        </View>
                        <Text style={{
                            color: '#000000',
                            fontSize: 20,
                            fontWeight: '700',
                            margin: 10
                        }}>
                            Other hotels nearby
                        </Text>
                        <View style={{ width: '95%' }}>
                            <ScrollView horizontal={true}>
                                {
                                    OtherHotels ? (
                                        OtherHotels.map(d => (
                                            <HotelMemberCart key={d.id} data={d} />
                                        ))
                                    ) : (
                                        <ActivityIndicator size="large" color="#FA454B" />
                                    )
                                }
                            </ScrollView>
                        </View>
                    </View>
                </View>
            </ScrollView>
            <View style={{
                backgroundColor: 'white',
                height: 60,
                paddingHorizontal: 20,
                flexDirection: 'row',
                justifyContent: 'space-between',
                alignItems: 'center',
            }}>
                <TouchableOpacity onPress={() => navigation.navigate('Review')} style={{
                    borderWidth: 1,
                    borderColor: '#0000008e',
                    width: 60,
                    height: 50,
                    borderRadius: 20,
                    justifyContent: 'center',
                    alignItems: 'center'
                }}>
                    <AntDesign name="hearto" size={24} color="black" />
                </TouchableOpacity>
                <TouchableOpacity onPress={() => navigation.navigate('Booking')} style={{
                    backgroundColor: '#64B657',
                    width: 200,
                    height: 50,
                    borderRadius: 20,
                    justifyContent: 'center',
                    alignItems: 'center'
                }}>
                    <Text style={{
                        color: 'white',
                        fontSize: 18
                    }}>Book Now</Text>
                </TouchableOpacity>
            </View>
        </View>
    );
};

